$("#new-project-form").on("submit",(function(e){e.preventDefault();const t=document.querySelector('button[type="submit"]');t.disabled=!0,t.textContent="Creating Project...",document.getElementById("loader").classList.remove("hide"),$.ajax({type:"post",url:"/new-project/create-project",data:$(this).serialize(),success:function(e){console.log(e),setTimeout((()=>{iziToast.success({title:"Success",message:"Project Created Successfully!",theme:"dark",backgroundColor:"linear-gradient(130deg, rgba(18,119,243,1) 0%, rgba(11,73,149,1) 97%)",position:"topCenter",progressBarColor:"white",transitionInMobile:"fadeInUp",transitionOutMobile:"fadeOutUp"}),$("#new-project-form")[0].reset(),t.disabled=!1,t.textContent="Create Project",document.getElementById("loader").classList.add("hide")}),1500)},error:function(e){console.log(e.responseText)}})}));