function filterClick(e,t){let s=`/issues/${e}/all/filter/${t}`;document.getElementById("search-input").value="";const i=document.querySelector(".active-label");i.classList.contains("question-active")?i.classList.remove("question-active"):i.classList.contains("bug-active")?i.classList.remove("bug-active"):i.classList.contains("help-active")?i.classList.remove("help-active"):i.classList.contains("invalid-active")&&i.classList.remove("invalid-active");document.querySelector(".label-button").classList.remove("hide"),i.classList.add("hide"),window.location.href.includes("open")?s=`/issues/${e}/open/filter/${t}`:window.location.href.includes("closed")&&(s=`/issues/${e}/closed/filter/${t}`);if(document.querySelector(".filters-expanded").classList.toggle("hide"),"old"===t){document.getElementById("filter-button-heading").classList.add("active-filter");document.getElementById("old-filter").classList.add("active-filter");document.getElementById("recent-filter").classList.remove("active-filter");document.getElementById("author-filter").classList.remove("active-filter")}else if("recent"===t){document.getElementById("filter-button-heading").classList.add("active-filter");document.getElementById("recent-filter").classList.add("active-filter");document.getElementById("old-filter").classList.remove("active-filter");document.getElementById("author-filter").classList.remove("active-filter")}else if("author"===t){document.getElementById("filter-button-heading").classList.add("active-filter");document.getElementById("author-filter").classList.add("active-filter");document.getElementById("old-filter").classList.remove("active-filter");document.getElementById("recent-filter").classList.remove("active-filter")}$.ajax({url:s,type:"get",success:function(e){const s=document.getElementById("issues-container");0===e.data.issues.length?s.innerHTML=`\n                <div class="no-issues">\n                    <i class="fa-solid fa-circle-dot"></i>\n                    <p class="heading">No results matched your filter ${t}!</p>\n                    <p class="sub-heading">\n                        Search again or you should <span>create an issue</span>.\n                    </p>\n                </div>\n                `:(s.innerHTML="",e.data.issues.forEach(((e,t)=>{console.log(e),s.innerHTML+=newIssueDom(e,t)})))}})}